import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [formIsValid, setFormIsValid] = useState(false);
  const [error, setError] = useState(false);
  const [inputFocus, setInputFocus] = useState(false);
  const nameInputRef = useRef();

  const nameChangeHandler = (e) => {
    setEnteredName(e.target.value);
  };
  const nameFocusHandler = () => {
    setInputFocus(true);
    if (enteredName === "") {
      setFormIsValid(false);
    }
  };
  const formInputIsValid = !formIsValid && inputFocus;
  const submitHandler = (e) => {
    e.preventDefault();

    const enteredValue = nameInputRef.current.value;
    if (enteredName.trim() === "") {
      setError(true);
    }
    console.log(enteredName, enteredValue);
    setFormIsValid(false);
  };
  const nameInputClasses = formInputIsValid
    ? "form-control invalid"
    : "form-control";
  console.log("error" + error);
  console.log("focus" + inputFocus);
  console.log("form input is valid" + formInputIsValid);
  return (
    <form onSubmit={submitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>

        <input
          ref={nameInputRef}
          type="text"
          id="name"
          onChange={nameChangeHandler}
          onBlur={nameFocusHandler}
        />
        {formInputIsValid && (
          <p className="error-text">Name field cannot be empty</p>
        )}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
